import React, { useState } from "react";

const OperationStep = ({ formData, setFormData }) => {
  const [customOrg, setCustomOrg] = useState("");

  const handleOrgChange = (e) => {
    const { value } = e.target;
    if (value === "Others") {
      setFormData({ ...formData, organizationRegistration: customOrg });
    } else {
      setFormData({ ...formData, organizationRegistration: value });
      setCustomOrg(""); // Reset custom input when switching back
    }
  };

  const handleCustomOrgChange = (e) => {
    setCustomOrg(e.target.value);
    setFormData({ ...formData, organizationRegistration: e.target.value });
  };

  return (
    <div>
      <h2>Step 2: Operation Details</h2>

      {/* Organization Registration */}
      <label>Organization Registration:</label>
      <select value={formData.organizationRegistration} onChange={handleOrgChange} required>
        <option value="">Select</option>
        <option value="Cooperative">Cooperative</option>
        <option value="Stock Corporation">Stock Corporation</option>
        <option value="Non-stock Corporation">Non-stock Corporation</option>
        <option value="Unregistered">Unregistered</option>
        <option value="Others">Others</option>
      </select>

      {/* Show custom text input only if "Others" is selected */}
      {formData.organizationRegistration === customOrg && (
        <input
          type="text"
          value={customOrg}
          onChange={handleCustomOrgChange}
          placeholder="Enter organization type"
          required
        />
      )}
    </div>
  );
};

export default OperationStep;
