import { useCBOContext } from "../hooks/useCBOcontext";

const WorkoutDetails = ({ workout }) => {
  const { dispatch } = useCBOContext();

  const handleDelete = async () => {
    const confirmDelete = window.confirm(
      `Are you sure you want to delete "${workout.name}"?`
    );

    if (!confirmDelete) return;

    const response = await fetch(`/api/workouts/${workout._id}`, {
      method: "DELETE",
    });

    if (response.ok) {
      dispatch({ type: "DELETE_WORKOUT", payload: workout._id });
    }
  };

  return (
    <div className="workout-details">
      <h4>{workout.name}</h4>
      <p>
        <strong>Shortname:</strong> {workout.shortname}
      </p>
      <p>
        <strong>Address:</strong> {workout.address}
      </p>
      <p>
        <strong>Number of Members:</strong> {workout.number_of_members}
      </p>
      <p>{new Date(workout.createdAt).toLocaleString()}</p>

      {/* Styled Delete Button (CSS now in index.css) */}
      <button className="delete-button" onClick={handleDelete}>
        Delete
      </button>
    </div>
  );
};

export default WorkoutDetails;
