import { useState } from "react";
import { useCBOContext } from "../hooks/useCBOcontext";

const CBOform = () => {
  const { dispatch } = useCBOContext(); // Get dispatch from context
  const [name, setName] = useState("");
  const [shortname, setShortname] = useState("");
  const [address, setAddress] = useState("");
  const [CBOrepresentation, setCBORepresentation] = useState("Main"); // Default to 'Main'
  const [number_of_members, setNumber_of_members] = useState("");
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !shortname || !address || !CBOrepresentation || !number_of_members) {
      setError("All fields are required.");
      return;
    }

    const numberOfMembers = parseInt(number_of_members, 10);
    if (isNaN(numberOfMembers) || numberOfMembers <= 0) {
      setError("Number of members must be a valid positive number.");
      return;
    }

    const newCBO = { name, shortname, address, CBOrepresentation, number_of_members: numberOfMembers };

    const response = await fetch("/api/workouts", {
      method: "POST",
      body: JSON.stringify(newCBO),
      headers: { "Content-Type": "application/json" },
    });

    const json = await response.json();

    if (!response.ok) {
      setError(json.error || "Something went wrong");
    } else {
      dispatch({ type: "CREATE_WORKOUT", payload: json }); // Update global state
      setError(null);
      setName("");
      setShortname("");
      setAddress("");
      setCBORepresentation("Main"); // Reset dropdown
      setNumber_of_members("");
      console.log("New CBO added:", json);
    }
  };

  return (
    <form className="create" onSubmit={handleSubmit}>
      <h3>Add a New CBO</h3>

      <label>CBO Name:</label>
      <input type="text" onChange={(e) => setName(e.target.value)} value={name} />

      <label>CBO Shortname:</label>
      <input type="text" onChange={(e) => setShortname(e.target.value)} value={shortname} />

      <label>CBO Address:</label>
      <input type="text" onChange={(e) => setAddress(e.target.value)} value={address} />

      <label>CBO Representation:</label>
      <select onChange={(e) => setCBORepresentation(e.target.value)} value={CBOrepresentation}>
        <option value="Main">Main</option>
        <option value="Branch">Branch</option>
      </select>

      <label>Number of members:</label>
      <input type="number" onChange={(e) => setNumber_of_members(e.target.value)} value={number_of_members} />

      <button>Register CBO</button>
      {error && <div className="error">{error}</div>}
    </form>
  );
};

export default CBOform;
