import React, { useEffect } from "react";

const OperationStep = ({ formData, setFormData, nextStep, prevStep }) => {
  // Ensure annual_production is initialized only once when the component first loads
  useEffect(() => {
    if (!formData.annual_production || formData.annual_production.length === 0) {
      setFormData({
        ...formData,
        annual_production: [
          { product: "", type: "", quantity: 0, unit: "", market_value: 0 },
        ],
      });
    }
  }, [formData, setFormData]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleOrgChange = (e) => {
    const { value } = e.target;
    if (value === "Others") {
      setFormData({ ...formData, organization_registration: "Others", other_organization_registration: "" });
    } else {
      setFormData({ ...formData, organization_registration: value, other_organization_registration: undefined });
    }
  };

  const handleCustomOrgChange = (e) => {
    setFormData({ ...formData, other_organization_registration: e.target.value });
  };

  const addProductionEntry = () => {
    setFormData({
      ...formData,
      annual_production: [
        ...formData.annual_production,
        { product: "", type: "", quantity: 0, unit: "", market_value: 0 },
      ],
    });
  };

  const handleProductionChange = (index, field, value) => {
    const updatedProduction = [...formData.annual_production];
    updatedProduction[index][field] = field === "quantity" || field === "market_value" ? parseFloat(value) || 0 : value;
    setFormData({ ...formData, annual_production: updatedProduction });
  };

  const removeProductionEntry = (index) => {
    const updatedProduction = formData.annual_production.filter((_, i) => i !== index);
    setFormData({ ...formData, annual_production: updatedProduction });
  };

  const handleMemberCountChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      number_of_members: {
        ...formData.number_of_members,
        [name]: parseInt(value, 10) || 0,
      },
    });
  };

  const handleProcurementChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setFormData({
        ...formData,
        procurement_experience: [
          ...formData.procurement_experience,
          { method: value, participation_count: 0, contracts_won: 0, successful_implementations: 0 },
        ],
      });
    } else {
      setFormData({
        ...formData,
        procurement_experience: formData.procurement_experience.filter((item) => item.method !== value),
      });
    }
  };

  const handleProcurementNumbers = (index, field, value) => {
    const updatedExperience = [...formData.procurement_experience];
    updatedExperience[index][field] = parseInt(value, 10) || 0;
    setFormData({ ...formData, procurement_experience: updatedExperience });
  };

  const validateOperationStep = () => {
    // Ensure operationDetails exists before accessing its properties
    if (!formData?.operationDetails) {
      alert("Form data is incomplete.");
      return false;
    }

    const operationDetails = formData.operationDetails;

    // Validate organization registration
    if (!operationDetails.organization_registration) {
      alert("Please select an organization registration type.");
      return false;
    }

    // If "Others" is selected, validate that a custom organization type is provided
    if (operationDetails.organization_registration === "Others" && !operationDetails.other_organization_registration?.trim()) {
      alert("Please specify the organization type when 'Others' is selected.");
      return false;
    }

    // Validate date established
    if (!operationDetails.date_established) {
      alert("Please select the date established.");
      return false;
    }

    // Validate sponsor agency
    if (!operationDetails.sponsor_agency) {
      alert("Please specify the sponsor agency.");
      return false;
    }

    // Validate number of members
    if (operationDetails.number_of_members?.male < 0 || operationDetails.number_of_members?.female < 0) {
      alert("Number of members cannot be negative.");
      return false;
    }

    // Validate annual production (all fields should not be empty and quantity/market value should be non-negative)
    for (let i = 0; i < operationDetails.annual_production.length; i++) {
      const item = operationDetails.annual_production[i];
      if (!item.product || !item.type || item.quantity < 0 || item.market_value < 0) {
        alert("Please fill all production fields correctly and ensure quantities and market values are non-negative.");
        return false;
      }
    }

    return true;
  };

  const handleNext = () => {
    if (validateOperationStep()) {
      nextStep(); // Move to the next step
    }
  };

  return (
    <div>
      <h2>Step 2: Operation Details</h2>
      <div>
        <label>Organization Registration</label>
        <select
          name="organization_registration"
          value={formData.organization_registration}
          onChange={handleOrgChange}
        >
          <option value="">Select...</option>
          <option value="Government">Government</option>
          <option value="Private">Private</option>
          <option value="NGO">NGO</option>
          <option value="Others">Others</option>
        </select>
        {formData.organization_registration === "Others" && (
          <input
            type="text"
            name="other_organization_registration"
            value={formData.other_organization_registration || ""}
            onChange={handleCustomOrgChange}
            placeholder="Specify organization"
          />
        )}
      </div>
      <div>
        <label>Date Established</label>
        <input
          type="date"
          name="date_established"
          value={formData.date_established || ""}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Sponsor Agency</label>
        <input
          type="text"
          name="sponsor_agency"
          value={formData.sponsor_agency || ""}
          onChange={handleInputChange}
        />
      </div>

      <div>
        <label>Annual Production</label>
        {formData.annual_production.map((prod, index) => (
          <div key={index}>
            <input
              type="text"
              placeholder="Product"
              value={prod.product || ""}
              onChange={(e) => handleProductionChange(index, "product", e.target.value)}
            />
            <input
              type="text"
              placeholder="Type"
              value={prod.type || ""}
              onChange={(e) => handleProductionChange(index, "type", e.target.value)}
            />
            <input
              type="number"
              placeholder="Quantity"
              value={prod.quantity || ""}
              onChange={(e) => handleProductionChange(index, "quantity", e.target.value)}
            />
            <input
              type="number"
              placeholder="Market Value"
              value={prod.market_value || ""}
              onChange={(e) => handleProductionChange(index, "market_value", e.target.value)}
            />
            <button type="button" onClick={() => removeProductionEntry(index)}>
              Remove
            </button>
          </div>
        ))}
        <button type="button" onClick={addProductionEntry}>Add Production</button>
      </div>

      <div>
        <button type="button" onClick={handleNext}>Next</button>
      </div>
    </div>
  );
};

export default OperationStep;
