import { useCBOContext } from "../hooks/useCBOcontext";

const CBODetails = ({ cbo }) => {
  const { dispatch } = useCBOContext();

  const handleDelete = async () => {
    const confirmDelete = window.confirm(
      `Are you sure you want to delete "${cbo.name}"?`
    );

    if (!confirmDelete) return;

    const response = await fetch(`/api/cbos/${cbo._id}`, {
      method: "DELETE",
    });

    if (response.ok) {
      dispatch({ type: "DELETE_CBO", payload: cbo._id });
    }
  };

  return (
    <div className="cbo-details">
      <h4>{cbo.name}</h4>
      <p>
        <strong>Shortname:</strong> {cbo.shortname}
      </p>
      <p>
        <strong>Address:</strong> {cbo.address}
      </p>
      <p>
        <strong>Number of Members:</strong> {cbo.number_of_members}
      </p>
      <p>{new Date(cbo.createdAt).toLocaleString()}</p>
      
      <button className="delete-button" onClick={handleDelete}>
        Delete
      </button>
    </div>
  );
};

export default CBODetails;
