import React, { useState } from "react";
import BasicInfoStep from "./registrationSteps/BasicInfoStep"; //  Step 1
import OperationStep from "./registrationSteps/OperationStep"; //  Step 2
import ContactStep from "./registrationSteps/ContactStep"; //  Step 3
import FileUploadStep from "./registrationSteps/FileUploadStep"; //  Step 4
import { registerCBO } from "../utils/api";
import { useCBOContext } from "../context/CBOcontext";

const RegistrationForm = () => {
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [customOrg, setCustomOrg] = useState("");
  const [errors, setErrors] = useState({}); // Track validation errors
  const { dispatch } = useCBOContext(); // Use context to update CBO list

  const [formData, setFormData] = useState({
    name: "",
    shortname: "",
    description: "",
    address: "",
    representation: "",
    operationDetails: {
      organization_registration: "",
      other_organization_registration: "",
      date_established: "",
      psic: "",
      target_members: "",
      number_of_members: { male: 0, female: 0 },
      annual_production: [],
      production_scope: "",
      sales_scope: "",
      total_assets: 0,
      total_liabilities: 0,
      annual_gross_income: 0,
      procurement_experience: [],
      sponsor_agency: "",
      other_sponsor_agency: "",
    },
  });

  const validateStep = () => {
    const newErrors = {};
    if (step === 1) {
      if (!formData.name) newErrors.name = "Name is required";
      if (!formData.shortname) newErrors.shortname = "Short name is required";
      if (!formData.address) newErrors.address = "Address is required";
      if (!formData.representation) newErrors.representation = "Representation is required";
    }
    if (step === 2) {
      if (!formData.operationDetails.organization_registration) {
        newErrors.organization_registration = "Organization registration is required";
      }
      if (
        formData.operationDetails.organization_registration === "Others" &&
        !customOrg.trim()
      ) {
        newErrors.other_organization_registration = "Please specify the organization type";
      }
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const nextStep = () => {
    if (validateStep()) setStep(step + 1);
  };

  const prevStep = () => setStep(step - 1);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateStep()) return;

    setLoading(true);

    const finalData = {
      ...formData,
      operationDetails: {
        ...formData.operationDetails,
        other_organization_registration:
          formData.operationDetails.organization_registration === "Others"
            ? customOrg
            : "",
      },
    };

    try {
      const response = await registerCBO(finalData);
      dispatch({ type: "CREATE_CBO", payload: response }); // Update context
      alert("Registration Successful!");
      setStep(1);
      setFormData({
        name: "",
        shortname: "",
        description: "",
        address: "",
        representation: "",
        operationDetails: {
          organization_registration: "",
          other_organization_registration: "",
          date_established: "",
          psic: "",
          target_members: "",
          number_of_members: { male: 0, female: 0 },
          annual_production: [],
          production_scope: "",
          sales_scope: "",
          total_assets: 0,
          total_liabilities: 0,
          annual_gross_income: 0,
          procurement_experience: [],
          sponsor_agency: "",
          other_sponsor_agency: "",
        },
      });
    } catch (error) {
      console.error("Error registering CBO:", error);
      alert("Registration Failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {step === 1 && (
        <BasicInfoStep
          formData={formData}
          setFormData={setFormData}
          errors={errors}
        />
      )}
      {step === 2 && (
        <OperationStep
          formData={formData.operationDetails}
          setFormData={(newOperationData) =>
            setFormData({ ...formData, operationDetails: newOperationData })
          }
          customOrg={customOrg}
          setCustomOrg={setCustomOrg}
          errors={errors}
        />
      )}
      <div className="step-nav">
        {step > 1 && <button type="button" onClick={prevStep}>Back</button>}
        {step < 4 && <button type="button" onClick={nextStep}>Next</button>}
        {step === 4 && <button type="submit" disabled={loading}>{loading ? "Submitting..." : "Submit"}</button>}
      </div>
    </form>
  );
};

export default RegistrationForm;
