import React, { useState } from "react";
import axios from "axios";

const FileUploadStep = ({ formData, setFormData, errors }) => {
  const [uploadProgress, setUploadProgress] = useState(0);

  const allowedTypes = ["application/pdf", "image/jpeg", "image/png"];
  const maxFileSize = 5 * 1024 * 1024; // 5MB

  const handleFileChange = (e, field) => {
    const file = e.target.files[0];
    if (!file) return;

    if (!allowedTypes.includes(file.type)) {
      alert("Only PDF, JPG, and PNG files are allowed.");
      return;
    }

    if (file.size > maxFileSize) {
      alert("File size must be less than 5MB.");
      return;
    }

    setFormData((prevData) => ({
      ...prevData,
      files: {
        ...prevData.files,
        [field]: { ...prevData.files[field], file, previewURL: URL.createObjectURL(file) },
      },
    }));
  };

  const removeFile = (field) => {
    setFormData((prevData) => ({
      ...prevData,
      files: {
        ...prevData.files,
        [field]: { ...prevData.files[field], file: null, previewURL: null },
      },
    }));
  };

  const getInputType = (subField) => {
    const typeMapping = {
      dateOfIssuance: "date",
      dateOfValidity: "date",
      year: "number",
      registryNo: "number",
    };
    return typeMapping[subField] || "text"; // Default to text
  };

  const handleInputChange = (e, field, subField) => {
    setFormData((prevData) => ({
      ...prevData,
      files: {
        ...prevData.files,
        [field]: { ...prevData.files[field], [subField]: e.target.value },
      },
    }));
  };

  const sections = {
    "Legal Requirements": ["rctResolution", "dti", "sec", "cda", "csoNpoNgoPo", "doleRule1020"],
    "Financial Requirements": ["bankBook", "auditedFinancialStatement", "latestITR", "salesInvoice"],
    "Additional Registration/Accreditations": [
      "businessPermit", "ffeDis", "birRegistration", "philGeps", "rsbsa", 
      "fishAr", "fda", "agrarianReformBeneficiaries", "farmersAssociation",
      "irrigatorsAssociation", "laborUnionsWorkersAssoc", "slpa"
    ],
  };

  const labels = {
    rctResolution: "RCT Resolution",
    dti: "Department of Trade and Industry (DTI)",
    sec: "Securities and Exchange Commission (SEC)",
    cda: "Cooperative Development Authority (CDA)",
    csoNpoNgoPo: "CSO / NGO / PO Registration",
    doleRule1020: "DOLE Rule 1020 Registration",
    bankBook: "Bank Book",
    auditedFinancialStatement: "Audited Financial Statement",
    latestITR: "Latest ITR",
    salesInvoice: "Sales Invoice",
    businessPermit: "Business Permit",
    ffeDis: "FFEDIS Registration",
    birRegistration: "BIR Registration",
    philGeps: "PhilGEPS Registration",
    rsbsa: "RSBSA Registration",
    fishAr: "FISH-AR Registration",
    fda: "FDA Registration",
    agrarianReformBeneficiaries: "Agrarian Reform Beneficiaries",
    farmersAssociation: "Farmers Association",
    irrigatorsAssociation: "Irrigators Association",
    laborUnionsWorkersAssoc: "Labor Unions Workers Association",
    slpa: "SLPA Registration",
  };

  return (
    <div className="step-container">
      <h2>Step 4: Legal Documents</h2>
      {Object.keys(sections).map((section) => (
        <div key={section} className="form-section">
          <h3 className="section-title">{section}</h3>
          <div className="form-table">
            {sections[section].map((field) => (
              <div key={field} className="form-row">
                <div className="form-title-row">{labels[field]}</div>
                <div className="form-inputs">
                  <div className="file-upload-container">
                    <input
                      type="file"
                      accept=".pdf,.jpg,.jpeg,.png"
                      onChange={(e) => handleFileChange(e, field)}
                      className="file-input"
                    />
                    {formData.files[field]?.file && (
                      <div className="file-actions">
                        <a href={formData.files[field].previewURL} target="_blank" rel="noopener noreferrer">
                          Preview File
                        </a>
                        <button type="button" onClick={() => removeFile(field)}>❌</button>
                      </div>
                    )}
                  </div>

                  {formData.files[field]?.file && (
                    <div className="form-field">
                      {Object.keys(formData.files[field]).map((subField) => {
                        if (subField === "file" || subField === "previewURL") return null;

                        return (
                          <div key={subField} className="subfield-container">
                            <label className="subfield-label">
                              {subField.replace(/([A-Z])/g, " $1").trim()}
                            </label>
                            <input
                              type={getInputType(subField)}
                              className="subfield-input"
                              placeholder={subField.replace(/([A-Z])/g, " $1").trim()}
                              value={formData.files[field][subField] || ""}
                              onChange={(e) => handleInputChange(e, field, subField)}
                            />
                          </div>
                        );
                      })}
                    </div>
                  )}

                  {errors[field] && <p className="error-text">{errors[field]}</p>}
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default FileUploadStep;
