import { useState } from 'react'

const CBOform = () => {
  const [name, setName] = useState('')
  const [shortname, setShortname] = useState('')
  const [address, setAddress] = useState('')
  const [CBOrepresentation, setCBORepresentation] = useState('')
  const [number_of_members, setNumber_of_members] = useState('')
  const [error, setError] = useState(null)

  const handleSubmit = async (e) => {
    e.preventDefault()
  
    // Validate that all fields are filled
    if (!name || !shortname || !address || !CBOrepresentation || !number_of_members) {
      setError('All fields are required.')
      return
    }
  
    // Convert number_of_members to a number
    const numberOfMembers = parseInt(number_of_members, 10)
  
    // Validate number_of_members to ensure it's a positive number
    if (isNaN(numberOfMembers) || numberOfMembers <= 0) {
      setError('Number of members must be a valid positive number')
      return
    }
  
    // Prepare the workout object to send to the backend
    const workout = {
      name,
      shortname,
      address,
      CBOrepresentation,
      number_of_members: numberOfMembers
    }
  
    console.log(workout); // Log the workout object to ensure it's being sent correctly
  
    // Make the request to the API
    const response = await fetch('/api/workouts', {
      method: 'POST',
      body: JSON.stringify(workout),
      headers: {
        'Content-Type': 'application/json'
      }
    })
  
    const json = await response.json()
  
    if (!response.ok) {
      setError(json.error || 'Something went wrong')
    } else {
      setError(null)
      setName('')
      setShortname('')
      setAddress('')
      setCBORepresentation('')
      setNumber_of_members('')
      console.log('New CBO added:', json)
    }
  }
  

  return (
    <form className="create" onSubmit={handleSubmit}> 
      <h3>Add a New CBO</h3>

      <label>CBO Name:</label>
      <input 
        type="text" 
        onChange={(e) => setName(e.target.value)} 
        value={name}
      />
      
      <label>CBO Shortname:</label>
      <input 
        type="text" 
        onChange={(e) => setShortname(e.target.value)} 
        value={shortname}
      />

      <label>CBO Address:</label>
      <input 
        type="text" 
        onChange={(e) => setAddress(e.target.value)} 
        value={address}
      />
      
      <label>CBO Representation:</label>
      <input 
        type="text" 
        onChange={(e) => setCBORepresentation(e.target.value)} 
        value={CBOrepresentation}
      />

      <label>Number of members:</label>
      <input 
        type="number" 
        onChange={(e) => setNumber_of_members(e.target.value)} 
        value={number_of_members}
      />

      <button>Add CBO</button>
      {error && <div className="error">{error}</div>}
    </form>
  )
}

export default CBOform
