import { useState } from "react";
import BasicInfoStep from "../registrationSteps/BasicInfoStep"; // Import Step 1 Component

const RegistrationForm = () => {
  const [step, setStep] = useState(1);

  // Store registration details
  const [formData, setFormData] = useState({
    name: "",
    shortname: "",
    description: "",
    address: "",
    representation: "Main", // Default to Main
  });

  // Handles input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Step navigation
  const handleNext = () => setStep((prev) => prev + 1);
  const handlePrev = () => setStep((prev) => prev - 1);

  return (
    <div className="registration-form">
      {step === 1 && (
        <BasicInfoStep formData={formData} handleChange={handleChange} handleNext={handleNext} />
      )}
      {/* Step 2 will go here later */}
    </div>
  );
};

export default RegistrationForm;
