import React, { useState } from "react";
import BasicInfoStep from "./registrationSteps/BasicInfoStep";
import OperationStep from "./registrationSteps/OperationStep";

const RegistrationForm = () => {
  const [step, setStep] = useState(1); // Track which step the user is on

  // Store form data
  const [formData, setFormData] = useState({
    name: "",
    shortname: "",
    address: "",
    representation: "",
    procurement_experience: [],
    sponsor_agency: "",
    other_sponsor_agency: "",
  });

  // Function to move to the next step
  const nextStep = () => setStep((prevStep) => prevStep + 1);

  // Function to go back to the previous step
  const prevStep = () => setStep((prevStep) => prevStep - 1);

  // Handle Form Submission
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submitting Form Data:", formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      {step === 1 && <BasicInfoStep formData={formData} setFormData={setFormData} />}
      {step === 2 && <OperationStep formData={formData} setFormData={setFormData} />}
      
      {/* Navigation Buttons */}
      <div>
        {step > 1 && <button type="button" onClick={prevStep}>Back</button>}
        {step < 2 && <button type="button" onClick={nextStep}>Next</button>}
        {step === 2 && <button type="submit">Submit</button>}
      </div>
    </form>
  );
};

export default RegistrationForm;
