import { useState } from "react";
import BasicInfoStep from "./registrationSteps/BasicInfoStep";
import OperationStep from "./registrationSteps/OperationStep";

const RegistrationForm = () => {
  const [step, setStep] = useState(1);

  // Store data for both steps
  const [basicInfoData, setBasicInfoData] = useState({
    name: "",
    shortname: "",
    description: "",
    address: "",
    representation: "",
  });

  const [operationData, setOperationData] = useState({
    organizationType: "",
    dateEstablished: "",
    psicCode: "",
    targetMembers: "",
    numberOfMembers: { male: 0, female: 0, total: 0 },
    annualProduction: [],
    productionScope: "",
    salesScope: "",
    totalAssets: 0,
    totalLiabilities: 0,
    annualGrossIncome: 0,
    procurementExperience: [],
    sponsorAgency: "",
    otherSponsorAgency: "",
  });

  // Handle form submission
  const handleSubmit = async () => {
    const formData = {
      ...basicInfoData, // Step 1 data
      operationDetails: operationData, // Step 2 data
    };

    try {
      const response = await fetch("/api/cbos", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        console.log("CBO registered:", data);
      } else {
        console.error("Registration failed:", data.error);
      }
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  return (
    <div className="registration-form">
      {step === 1 && (
        <BasicInfoStep formData={basicInfoData} setFormData={setBasicInfoData} />
      )}
      {step === 2 && (
        <OperationStep formData={operationData} setFormData={setOperationData} />
      )}

      <div className="navigation-buttons">
        {step > 1 && <button onClick={() => setStep(step - 1)}>Previous</button>}
        {step < 2 && <button onClick={() => setStep(step + 1)}>Next</button>}
        {step === 2 && <button onClick={handleSubmit}>Submit</button>}
      </div>
    </div>
  );
};

export default RegistrationForm;
