import { useEffect, useState } from "react";

const OperationStep = ({ formData, setFormData }) => {
  const [customOrg, setCustomOrg] = useState("");

  useEffect(() => {
    if (!formData.operationDetails) {
      setFormData((prev) => ({
        ...prev,
        operationDetails: {
          organization_registration: "",
          date_established: "",
          psic: "",
          target_members: "",
          number_of_members: { male: 0, female: 0 },
          annual_production: [],
          production_scope: "",
          sales_scope: "",
          total_assets: 0,
          total_liabilities: 0,
          annual_gross_income: 0,
          procurement_experience: [],
          sponsor_agency: "",
          other_sponsor_agency: "",
        },
      }));
    }
  }, [formData, setFormData]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      operationDetails: {
        ...prev.operationDetails,
        [name]: value,
      },
    }));
  };

  const handleMemberCountChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      operationDetails: {
        ...prev.operationDetails,
        number_of_members: {
          ...prev.operationDetails.number_of_members,
          [name]: parseInt(value, 10) || 0,
        },
      },
    }));
  };

  const handleOrgChange = (e) => {
    const { value } = e.target;
    if (value !== "Others") {
      setFormData((prev) => ({
        ...prev,
        operationDetails: {
          ...prev.operationDetails,
          organization_registration: value,
        },
      }));
      setCustomOrg("");
    } else {
      setFormData((prev) => ({
        ...prev,
        operationDetails: {
          ...prev.operationDetails,
          organization_registration: customOrg,
        },
      }));
    }
  };

  const handleCustomOrgChange = (e) => {
    setCustomOrg(e.target.value);
    setFormData((prev) => ({
      ...prev,
      operationDetails: {
        ...prev.operationDetails,
        organization_registration: e.target.value,
      },
    }));
  };

  const handleProcurementChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prev) => {
      let updatedExperience = [...prev.operationDetails.procurement_experience];

      if (checked) {
        updatedExperience.push({ method: value, participation_count: 0, contracts_won: 0, successful_implementations: 0 });
      } else {
        updatedExperience = updatedExperience.filter((item) => item.method !== value);
      }

      return {
        ...prev,
        operationDetails: {
          ...prev.operationDetails,
          procurement_experience: updatedExperience,
        },
      };
    });
  };

  const handleProcurementNumbers = (index, field, value) => {
    setFormData((prev) => {
      const updatedExperience = [...prev.operationDetails.procurement_experience];
      updatedExperience[index][field] = parseInt(value, 10) || 0;

      return {
        ...prev,
        operationDetails: {
          ...prev.operationDetails,
          procurement_experience: updatedExperience,
        },
      };
    });
  };

  return (
    <div>
      <h2>Step 2: Operations</h2>

      <label>Organization Registration:</label>
      <select name="organization_registration" value={formData.operationDetails?.organization_registration || ""} onChange={handleOrgChange} required>
        <option value="">Select One</option>
        <option value="Cooperative">Cooperative</option>
        <option value="Stock Corporation">Stock Corporation</option>
        <option value="Non-stock Corporation">Non-stock Corporation</option>
        <option value="Unregistered">Unregistered</option>
        <option value="Others">Others</option>
      </select>
      {formData.operationDetails?.organization_registration === customOrg && (
        <input type="text" value={customOrg} onChange={handleCustomOrgChange} placeholder="Others Please Specify" required />
      )}

      <label>Date Established:</label>
      <input type="date" name="date_established" value={formData.operationDetails?.date_established || ""} onChange={handleInputChange} />

      <label>PSIC:</label>
      <input type="text" name="psic" value={formData.operationDetails?.psic || ""} onChange={handleInputChange} />

      <label>Target Members:</label>
      <input type="text" name="target_members" value={formData.operationDetails?.target_members || ""} onChange={handleInputChange} />

      <label>Number of Members:</label>
      <div>
        <label>Male:</label>
        <input type="number" name="male" value={formData.operationDetails?.number_of_members.male || 0} onChange={handleMemberCountChange} />
        <label>Female:</label>
        <input type="number" name="female" value={formData.operationDetails?.number_of_members.female || 0} onChange={handleMemberCountChange} />
        <label>Total:</label>
        <input type="number" value={(formData.operationDetails?.number_of_members.male || 0) + (formData.operationDetails?.number_of_members.female || 0)} readOnly />
      </div>

      <label>Area/Scope of Production:</label>
      <input type="text" name="production_scope" value={formData.operationDetails?.production_scope || ""} onChange={handleInputChange} />

      <label>Area/Scope of Sales:</label>
      <input type="text" name="sales_scope" value={formData.operationDetails?.sales_scope || ""} onChange={handleInputChange} />

      <label>Total Assets:</label>
      <input type="number" name="total_assets" value={formData.operationDetails?.total_assets || 0} onChange={handleInputChange} />

      <label>Total Liabilities:</label>
      <input type="number" name="total_liabilities" value={formData.operationDetails?.total_liabilities || 0} onChange={handleInputChange} />

      <label>Annual Gross Income:</label>
      <input type="number" name="annual_gross_income" value={formData.operationDetails?.annual_gross_income || 0} onChange={handleInputChange} />

      <label>Experience in Procurement:</label>
      {["Competitive Bidding", "Negotiated Procurement", "Shopping", "Small Value Procurement", "Others", "No Experience"].map((method) => (
        <div key={method}>
          <input type="checkbox" value={method} checked={formData.operationDetails?.procurement_experience.some((exp) => exp.method === method)} onChange={handleProcurementChange} />
          <label>{method}</label>
        </div>
      ))}

      {formData.operationDetails?.procurement_experience.map((exp, index) => (
        <div key={index}>
          <h4>{exp.method}</h4>
          <label>Number of Participations:</label>
          <input type="number" value={exp.participation_count} onChange={(e) => handleProcurementNumbers(index, "participation_count", e.target.value)} />
          <label>Contracts Won:</label>
          <input type="number" value={exp.contracts_won} onChange={(e) => handleProcurementNumbers(index, "contracts_won", e.target.value)} />
          <label>Successful Implementations:</label>
          <input type="number" value={exp.successful_implementations} onChange={(e) => handleProcurementNumbers(index, "successful_implementations", e.target.value)} />
        </div>
      ))}

      <label>Sponsor Agency:</label>
      <input type="text" name="sponsor_agency" value={formData.operationDetails?.sponsor_agency || ""} onChange={handleInputChange} required />

      <label>Other Sponsor Agency:</label>
      <input type="text" name="other_sponsor_agency" value={formData.operationDetails?.other_sponsor_agency || ""} onChange={handleInputChange} />
    </div>
  );
};

export default OperationStep;
