import React from "react";

const OperationStep = ({ formData, setFormData }) => {
  const procurementMethods = [
    "Competitive Bidding",
    "Negotiated Procurement",
    "Shopping",
    "Small Value Procurement",
    "No Experience",
  ];

  const handleProcurementChange = (e) => {
    const { value, checked } = e.target;
    if (value === "No Experience" && checked) {
      setFormData({
        ...formData,
        procurementExperience: [{ method: "No Experience", participationCount: 0, contractsWon: 0, successfulImplementations: 0 }],
      });
      return;
    }
    if (value !== "No Experience") {
      setFormData({
        ...formData,
        procurementExperience: formData.procurementExperience.filter((exp) => exp.method !== "No Experience"),
      });
    }
    if (checked) {
      setFormData({
        ...formData,
        procurementExperience: [...formData.procurementExperience, { method: value, participationCount: 0, contractsWon: 0, successfulImplementations: 0 }],
      });
    } else {
      setFormData({
        ...formData,
        procurementExperience: formData.procurementExperience.filter((exp) => exp.method !== value),
      });
    }
  };

  const handleProcurementNumbers = (index, field, value) => {
    const updatedExperience = [...formData.procurementExperience];
    updatedExperience[index][field] = parseInt(value, 10) || 0;
    setFormData({ ...formData, procurementExperience: updatedExperience });
  };

  return (
    <div>
      <h2>Step 2: Operation Details</h2>

      <label>Experience in Procurement:</label>
      <div>
        {procurementMethods.map((method) => (
          <div key={method}>
            <input
              type="checkbox"
              value={method}
              checked={formData.procurementExperience.some((exp) => exp.method === method)}
              onChange={handleProcurementChange}
              disabled={formData.procurementExperience.some((exp) => exp.method === "No Experience") && method !== "No Experience"}
            />
            <label>{method}</label>
          </div>
        ))}
      </div>

      {formData.procurementExperience
        .filter((exp) => exp.method !== "No Experience")
        .map((exp, index) => (
          <div key={index}>
            <h4>{exp.method}</h4>
            <label>Number of Participation:</label>
            <input
              type="number"
              value={exp.participationCount}
              onChange={(e) => handleProcurementNumbers(index, "participationCount", e.target.value)}
            />
            <label>Number of Contracts Won:</label>
            <input
              type="number"
              value={exp.contractsWon}
              onChange={(e) => handleProcurementNumbers(index, "contractsWon", e.target.value)}
            />
            <label>Number of Successful Implementations:</label>
            <input
              type="number"
              value={exp.successfulImplementations}
              onChange={(e) => handleProcurementNumbers(index, "successfulImplementations", e.target.value)}
            />
          </div>
        ))}

      <label>Sponsor Agency:</label>
      <input type="text" value={formData.sponsorAgency} onChange={(e) => setFormData({ ...formData, sponsorAgency: e.target.value })} required />

      <label>Other Sponsor Agency (Optional):</label>
      <input type="text" value={formData.otherSponsorAgency} onChange={(e) => setFormData({ ...formData, otherSponsorAgency: e.target.value })} />
    </div>
  );
};

export default OperationStep;
