import { createContext, useReducer, useContext } from "react";

export const CBOContext = createContext();

export const cboReducer = (state, action) => {
  switch (action.type) {
    case "SET_WORKOUTS":
      return {
        workouts: action.payload,
      };
    case "CREATE_WORKOUT":
      return {
        workouts: [action.payload, ...state.workouts],
      };
    case "DELETE_WORKOUT":
      return {
        workouts: state.workouts.filter((workout) => workout._id !== action.payload),
      };
    default:
      return state;
  }
};

export const CBOContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(cboReducer, {
    workouts: [], // Changed from `null` to `[]`
  });

  return <CBOContext.Provider value={{ ...state, dispatch }}>{children}</CBOContext.Provider>;
};

// Custom hook to use the context
export const useCBOContext = () => {
  const context = useContext(CBOContext);
  if (!context) {
    throw new Error("useCBOContext must be used within a CBOContextProvider");
  }
  return context;
};
