import { useState } from "react"

const CBOform = () => {
    const [name, setName] = useState ('')
    const [shortname, setshortName] = useState ('')
    const [address, setAddress] = useState ('')
    const [CBOrepresentation, setCBOrep] = useState ('')
    const [number_of_members, setNumber_of_members] = useState ('')
    const [error, setError] = useState (null)

    const handleSubmit = async (e) => {
        e.preventDefault()

        const workout = {name,shortname,address, CBOrepresentation, number_of_members}

        const response = await fetch('/api/workouts', {
            method: 'POST',
            body: JSON.stringify(workout),
            headers: {
                'Content-type':'application/json'
            }
        })

        const json = await response.json()

        if(!response){
            setError(json.error)
        }

        if (response){
            setError(null)
            setName('')
            setshortName('')
            setAddress('')
            setCBOrep('')
            setNumber_of_members('')
            console.log('New CBO added:', json)
        }
    }
    
    
    return (
        <form className="create" onSubmit={handleSubmit}>
            <h3> Add a New CBO</h3>

            <label>CBO name: </label>
            <input
                type ="text"
                onChange={(e) => setName(e.target.value)}
                value = {name}
            />

            <label>CBO shortname: </label>
            <input
                type ="text"
                onChange={(e) => setshortName(e.target.value)}
                value = {shortname}
            />

            <label>Address: </label>
            <input
                type ="text"
                onChange={(e) => setAddress(e.target.value)}
                value = {address}
            />

            <label>CBO Representation: </label>
            <input
                type ="text"
                onChange={(e) => setCBOrep(e.target.value)}
                value = {CBOrepresentation}
            />

            <label> Number of members: </label>
            <input
                type ="number"
                onChange={(e) => setNumber_of_members(e.target.value)}
                value = {number_of_members}
            />

            <button>
                Register CBO
            </button>
        </form>
    )
}

export default CBOform
