import React, { useState } from "react";

const OperationStep = ({ formData, setFormData }) => {
  const [otherOrg, setOtherOrg] = useState(formData.organization_registration === "Others" ? formData.other_organization : "");

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    // Reset "Other" text input when selecting a different option
    if (name === "organization_registration" && value !== "Others") {
      setOtherOrg("");
      setFormData({ ...formData, other_organization: "" });
    }
  };

  const handleOtherOrgChange = (e) => {
    setOtherOrg(e.target.value);
    setFormData({ ...formData, other_organization: e.target.value });
  };

  const handleProcurementChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setFormData({
        ...formData,
        procurement_experience: [
          ...formData.procurement_experience,
          { method: value, participation_count: 0, contracts_won: 0, successful_implementations: 0 },
        ],
      });
    } else {
      setFormData({
        ...formData,
        procurement_experience: formData.procurement_experience.filter((item) => item.method !== value),
      });
    }
  };

  const handleProcurementNumbers = (index, field, value) => {
    const updatedExperience = [...formData.procurement_experience];
    updatedExperience[index][field] = parseInt(value, 10) || 0;
    setFormData({ ...formData, procurement_experience: updatedExperience });
  };

  return (
    <div>
      <h2>Step 2: Operation Details</h2>

      {/* ✅ Organization Registration Dropdown */}
      <label>Organization Registration:</label>
      <select name="organization_registration" value={formData.organization_registration} onChange={handleInputChange} required>
        <option value="">Select One</option>
        <option value="Cooperative">Cooperative</option>
        <option value="Stock Corporation">Stock Corporation</option>
        <option value="Non-stock Corporation">Non-stock Corporation</option>
        <option value="Unregistered">Unregistered</option>
        <option value="Others">Others</option>
      </select>

      {/* ✅ Show Text Input When "Others" is Selected */}
      {formData.organization_registration === "Others" && (
        <div>
          <label>Please specify:</label>
          <input type="text" name="other_organization" value={otherOrg} onChange={handleOtherOrgChange} required />
        </div>
      )}

      <label>Experience in Procurement:</label>
      <div>
        {["Competitive Bidding", "Negotiated Procurement", "Shopping", "Small Value Procurement", "No Experience"].map((method) => (
          <div key={method}>
            <input type="checkbox" value={method} onChange={handleProcurementChange} />
            <label>{method}</label>
          </div>
        ))}
      </div>

      {formData.procurement_experience.map((exp, index) => (
        <div key={index}>
          <h4>{exp.method}</h4>
          <label>Number of Participation:</label>
          <input
            type="number"
            value={exp.participation_count}
            onChange={(e) => handleProcurementNumbers(index, "participation_count", e.target.value)}
          />
          <label>Number of Contracts Won:</label>
          <input
            type="number"
            value={exp.contracts_won}
            onChange={(e) => handleProcurementNumbers(index, "contracts_won", e.target.value)}
          />
          <label>Number of Successful Implementations:</label>
          <input
            type="number"
            value={exp.successful_implementations}
            onChange={(e) => handleProcurementNumbers(index, "successful_implementations", e.target.value)}
          />
        </div>
      ))}

      <label>Sponsor Agency:</label>
      <input type="text" value={formData.sponsor_agency} onChange={(e) => setFormData({ ...formData, sponsor_agency: e.target.value })} required />

      <label>Other Sponsor Agency (Optional):</label>
      <input type="text" value={formData.other_sponsor_agency} onChange={(e) => setFormData({ ...formData, other_sponsor_agency: e.target.value })} />
    </div>
  );
};

export default OperationStep;
