import React, { useState } from "react";
import BasicInfoStep from "./registrationSteps/BasicInfoStep";
import OperationStep from "./registrationSteps/OperationStep";

const RegistrationForm = () => {
  const [step, setStep] = useState(1); // Track which step the user is on

  // Store form data
  const [formData, setFormData] = useState({
    name: "",
    shortname: "",
    address: "",
    representation: "",
    procurement_experience: [],
    sponsor_agency: "",
    other_sponsor_agency: "",
  });

  // Function to move to the next step
  const nextStep = () => setStep(step + 1);

  // Function to go back to the previous step
  const prevStep = () => setStep(step - 1);

  return (
    <div className="registration-container">
      {step === 1 && (
        <BasicInfoStep formData={formData} setFormData={setFormData} nextStep={nextStep} />
      )}
      {step === 2 && (
        <OperationStep formData={formData} setFormData={setFormData} nextStep={nextStep} prevStep={prevStep} />
      )}
      {step === 3 && <h2>Step 3: Final Review & Submit</h2>}
    </div>
  );
};

export default RegistrationForm;
