import { useState } from "react";

const BasicInfoStep = ({ formData, setFormData, nextStep }) => {
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Validate required fields
    if (!formData.name || !formData.shortname || !formData.address || !formData.representation) {
      setError("Please fill in all required fields.");
      return;
    }

    // Send data to backend
    const response = await fetch("/api/cbos", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData),
    });

    const json = await response.json();

    if (!response.ok) {
      setError(json.error);
    } else {
      console.log("CBO registered:", json);
      nextStep(); // Proceed to Step 2
    }
  };

  return (
    <div className="step-container">
      <h2>Step 1: Basic Information</h2>

      <form onSubmit={handleSubmit}>
        {/* Organization Name */}
        <label>Organization Name *</label>
        <input
          type="text"
          value={formData.name}
          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          required
        />

        {/* Short Name */}
        <label>Short Name *</label>
        <input
          type="text"
          value={formData.shortname}
          onChange={(e) => setFormData({ ...formData, shortname: e.target.value })}
          required
        />

        {/* Short Description */}
        <label>Short Description</label>
        <textarea
          value={formData.description}
          onChange={(e) => setFormData({ ...formData, description: e.target.value })}
        />

        {/* Office Address */}
        <label>Office Address *</label>
        <input
          type="text"
          value={formData.address}
          onChange={(e) => setFormData({ ...formData, address: e.target.value })}
          required
        />

        {/* CBO Representation Dropdown */}
        <label>CBO Representation *</label>
        <select
          value={formData.representation}
          onChange={(e) => setFormData({ ...formData, representation: e.target.value })}
          required
        >
          <option value="Main">Main</option>
          <option value="Branch">Branch</option>
        </select>

        {error && <p style={{ color: "red" }}>{error}</p>}
        <button type="submit">Next</button>
      </form>
    </div>
  );
};

export default BasicInfoStep;
