const OperationStep = ({ formData, setFormData }) => {
  if (!formData.annual_production) {
    setFormData({ ...formData, annual_production: [] });
  }

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleOrgChange = (e) => {
    const { value } = e.target;
    if (value === "Others") {
      setFormData({ ...formData, organization_registration: "Others", other_organization_registration: "" });
    } else {
      setFormData({ ...formData, organization_registration: value, other_organization_registration: undefined });
    }
    };
    const handleCustomOrgChange = (e) => {
      setFormData({ ...formData, other_organization_registration: e.target.value });
    };
  
  
  const addProductionEntry = () => {
    setFormData({
      ...formData,
      annual_production: [
        ...formData.annual_production,
        { product: "", type: "", quantity: 0, unit: "", market_value: 0 },
      ],
    });
  };

  const handleProductionChange = (index, field, value) => {
    const updatedProduction = [...formData.annual_production];
    updatedProduction[index][field] = field === "quantity" || field === "market_value" ? parseFloat(value) || 0 : value;
    setFormData({ ...formData, annual_production: updatedProduction });
  };

  const removeProductionEntry = (index) => {
    const updatedProduction = formData.annual_production.filter((_, i) => i !== index);
    setFormData({ ...formData, annual_production: updatedProduction });
  };

  const handleMemberCountChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      number_of_members: {
        ...formData.number_of_members,
        [name]: parseInt(value, 10) || 0,
      },
    });
  };

  const handleProcurementChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setFormData({
        ...formData,
        procurement_experience: [
          ...formData.procurement_experience,
          { method: value, participation_count: 0, contracts_won: 0, successful_implementations: 0 },
        ],
      });
    } else {
      setFormData({
        ...formData,
        procurement_experience: formData.procurement_experience.filter((item) => item.method !== value),
      });
    }
  };

  const handleProcurementNumbers = (index, field, value) => {
    const updatedExperience = [...formData.procurement_experience];
    updatedExperience[index][field] = parseInt(value, 10) || 0;
    setFormData({ ...formData, procurement_experience: updatedExperience });
  };

  return (
    <div className="operation-step-container">
      <h2>Step 2: Operations</h2>

      <label htmlFor="organization_registration">Organization Registration:</label>
      <select
        id="organization_registration"
        name="organization_registration"
        value={formData.organization_registration}
        onChange={handleOrgChange}
        required
        className="form-select"
      >
        <option value="">Select One</option>
        <option value="Cooperative">Cooperative</option>
        <option value="Stock Corporation">Stock Corporation</option>
        <option value="Non-stock Corporation">Non-stock Corporation</option>
        <option value="Unregistered">Unregistered</option>
        <option value="Others">Others</option>
      </select>

      {/* Show input only when "Others" is selected */}
      {formData.organization_registration === "Others" && (
        <div className="other-registration-container">
          <label htmlFor="other_organization_registration">Others (Please Specify):</label>
          <input
            type="text"
            id="other_organization_registration"
            name="other_organization_registration"
            value={formData.other_organization_registration || ""}
            onChange={handleCustomOrgChange}
            placeholder="Others Please Specify"
            required
            className="form-input"
          />
        </div>
      )}

      {/* Date Established */}
      <label htmlFor="date_established">Date Established:</label>
      <input
        type="date"
        id="date_established"
        name="date_established"
        value={formData.date_established}
        onChange={handleInputChange}
        required
        className="form-input"
      />

      {/* PSIC */}
      <label htmlFor="psic">Philippine Statistical Industry Classification (PSIC):</label>
      <input
        type="text"
        id="psic"
        name="psic"
        value={formData.psic}
        onChange={handleInputChange}
        className="form-input"
      />

      {/* Target Members */}
      <label htmlFor="target_members">Target Members:</label>
      <input
        type="text"
        id="target_members"
        name="target_members"
        value={formData.target_members}
        onChange={handleInputChange}
        className="form-input"
      />

      {/* Number of Members */}
      <label>Number of Members:</label>
      <div className="member-count-container">
        <label htmlFor="male">Male:</label>
        <input
          type="number"
          id="male"
          name="male"
          value={formData.number_of_members?.male || 0}
          onChange={handleMemberCountChange}
          className="form-input"
        />
        <label htmlFor="female">Female:</label>
        <input
          type="number"
          id="female"
          name="female"
          value={formData.number_of_members?.female || 0}
          onChange={handleMemberCountChange}
          className="form-input"
        />
        <label>Total:</label>
        <input
          type="number"
          value={(formData.number_of_members?.male || 0) + (formData.number_of_members?.female || 0)}
          readOnly
          className="form-input"
        />
      </div>

      {/* Annual Production */}
      <label>Annual Production:</label>
      <table className="annual-production-table">
        <thead>
          <tr>
            <th>Product</th>
            <th>Type</th>
            <th>Quantity</th>
            <th>Unit</th>
            <th>Market Value</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {formData.annual_production.map((item, index) => (
            <tr key={index}>
              <td><input type="text" value={item.product} onChange={(e) => handleProductionChange(index, "product", e.target.value)} required className="form-input" /></td>
              <td><input type="text" value={item.type} onChange={(e) => handleProductionChange(index, "type", e.target.value)} required className="form-input" /></td>
              <td><input type="number" value={item.quantity} onChange={(e) => handleProductionChange(index, "quantity", e.target.value)} required className="form-input" /></td>
              <td><input type="text" value={item.unit} onChange={(e) => handleProductionChange(index, "unit", e.target.value)} required className="form-input" /></td>
              <td><input type="number" value={item.market_value} onChange={(e) => handleProductionChange(index, "market_value", e.target.value)} required className="form-input" /></td>
              <td><button type="button" onClick={() => removeProductionEntry(index)} className="remove-entry-button">Remove</button></td>
            </tr>
          ))}
        </tbody>
      </table>
      <button type="button" onClick={addProductionEntry} className="add-entry-button">Add Product</button>

      {/* Other Inputs */}
      <label htmlFor="production_scope">Area/Scope of Production:</label>
      <input
        type="text"
        id="production_scope"
        name="production_scope"
        value={formData.production_scope}
        onChange={handleInputChange}
        className="form-input"
      />

      <label htmlFor="sales_scope">Area/Scope of Sales:</label>
      <input
        type="text"
        id="sales_scope"
        name="sales_scope"
        value={formData.sales_scope}
        onChange={handleInputChange}
        className="form-input"
      />

      <label htmlFor="total_assets">Total Assets:</label>
      <input
        type="number"
        id="total_assets"
        name="total_assets"
        value={formData.total_assets}
        onChange={handleInputChange}
        className="form-input"
      />

      <label htmlFor="total_liabilities">Total Liabilities:</label>
      <input
        type="number"
        id="total_liabilities"
        name="total_liabilities"
        value={formData.total_liabilities}
        onChange={handleInputChange}
        className="form-input"
      />

      <label htmlFor="annual_gross_income">Annual Gross Income:</label>
      <input
        type="number"
        id="annual_gross_income"
        name="annual_gross_income"
        value={formData.annual_gross_income}
        onChange={handleInputChange}
        className="form-input"
      />

      {/* Procurement Experience */}
      <label>Experience in Procurement:</label>
      <div className="procurement-methods">
        <label>
          <input
            type="checkbox"
            value="Public Bidding"
            checked={formData.procurement_experience.some((exp) => exp.method === "Public Bidding")}
            onChange={handleProcurementChange}
          />
          Public Bidding
        </label>
        <label>
          <input
            type="checkbox"
            value="Shopping"
            checked={formData.procurement_experience.some((exp) => exp.method === "Shopping")}
            onChange={handleProcurementChange}
          />
          Shopping
        </label>
        {/* Add more procurement methods if needed */}
      </div>

      {/* Procurement Details */}
      {formData.procurement_experience.map((exp, index) => (
        <div key={index} className="procurement-details">
          <h3>{exp.method}</h3>
          <label htmlFor={`participation_count_${index}`}>Participation Count:</label>
          <input
            type="number"
            id={`participation_count_${index}`}
            value={exp.participation_count}
            onChange={(e) => handleProcurementNumbers(index, "participation_count", e.target.value)}
            className="form-input"
          />
          <label htmlFor={`contracts_won_${index}`}>Contracts Won:</label>
          <input
            type="number"
            id={`contracts_won_${index}`}
            value={exp.contracts_won}
            onChange={(e) => handleProcurementNumbers(index, "contracts_won", e.target.value)}
            className="form-input"
          />
          <label htmlFor={`successful_implementations_${index}`}>Successful Implementations:</label>
          <input
            type="number"
            id={`successful_implementations_${index}`}
            value={exp.successful_implementations}
            onChange={(e) => handleProcurementNumbers(index, "successful_implementations", e.target.value)}
            className="form-input"
          />
        </div>
      ))}

    </div>
  );
};

export default OperationStep;
