import React, { useState } from "react";
import BasicInfoStep from "./registrationSteps/BasicInfoStep"; 
import OperationStep from "./registrationSteps/OperationStep"; 
import ContactStep from "./registrationSteps/ContactStep"; 
import FileUploadStep from "./registrationSteps/FileUploadStep"; 
import { registerCBO } from "../utils/api";
import { useCBOContext } from "../context/CBOcontext";

const RegistrationForm = () => {
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const { dispatch } = useCBOContext();

  const [formData, setFormData] = useState({
    name: "",
    shortname: "",
    description: "",
    address: "",
    representation: "",
    primaryContact: { name: "", email: "" },
    files: { rctResolution: null },
  });

  const validateStep = () => {
    let newErrors = {};
    if (step === 1) {
      if (!formData.name) newErrors.name = "Name is required";
      if (!formData.shortname) newErrors.shortname = "Short name is required";
    }
    if (step === 3) {
      if (!formData.primaryContact.name) newErrors.primaryContactName = "Contact name required";
      if (!formData.primaryContact.email) newErrors.primaryContactEmail = "Valid email required";
    }
    if (step === 4) {
      if (!formData.files.rctResolution) newErrors.rctResolution = "File is required";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const nextStep = () => {
    if (validateStep()) setStep(step + 1);
  };

  const prevStep = () => setStep(step - 1);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateStep()) return;

    setLoading(true);
    try {
      const response = await registerCBO(formData);
      dispatch({ type: "CREATE_CBO", payload: response });
      alert("Registration Successful!");
      setStep(1);
    } catch (error) {
      alert("Registration Failed. Try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {step === 1 && <BasicInfoStep formData={formData} setFormData={setFormData} errors={errors} />}
      {step === 2 && <OperationStep formData={formData} setFormData={setFormData} errors={errors} />}
      {step === 3 && <ContactStep formData={formData} setFormData={setFormData} errors={errors} />}
      {step === 4 && <FileUploadStep formData={formData} setFormData={setFormData} errors={errors} />}

      <div className="step-nav">
        {step > 1 && <button type="button" onClick={prevStep}>Back</button>}
        {step < 4 && <button type="button" onClick={nextStep}>Next</button>}
        {step === 4 && <button type="submit" disabled={loading}>{loading ? "Submitting..." : "Submit"}</button>}
      </div>
    </form>
  );
};

export default RegistrationForm;
